"// Verificar sessão Apoia existente e token PDPJ
const staticData = $getWorkflowStaticData('global');
const forceNew = $input.first().json.forceNewLogin;
let pdpjToken = $input.first().json.pdpjToken;
const dbSession = Array.isArray($input.first().json) ? $input.first().json[0] : null;

if (!pdpjToken) {
  if (staticData.pdpj_auth && staticData.pdpj_auth.access_token) {
    const tokenAge = Date.now() - (staticData.pdpj_auth.timestamp || 0);
    const tokenValid = tokenAge < (staticData.pdpj_auth.expires_in || 3600) * 1000;
    if (tokenValid) {
      pdpjToken = staticData.pdpj_auth.access_token;
    } else {
      throw new Error('Token PDPJ expirado. Execute login PDPJ primeiro.');
    }
  } else {
    throw new Error('Token PDPJ não encontrado. Execute login PDPJ primeiro.');
  }
}

if (!forceNew) {
  if (dbSession && dbSession.cookie_string) {
    console.log('✅ Sessão Apoia carregada do banco');
    return {
      hasValidSession: true,
      cookies: dbSession.cookies,
      cookieString: dbSession.cookie_string,
      expiresAt: parseInt(dbSession.expires_at),
      fromCache: false,
      source: 'database'
    };
  }
  if (staticData.apoiaSession) {
    const sessionAge = Date.now() - (staticData.apoiaSession.timestamp || 0);
    const maxAge = 23 * 60 * 60 * 1000;
    if (sessionAge < maxAge && staticData.apoiaSession.cookies) {
      console.log('✅ Sessão Apoia válida encontrada');
      return {
        hasValidSession: true,
        cookies: staticData.apoiaSession.cookies,
        cookieString: staticData.apoiaSession.cookieString,
        expiresAt: staticData.apoiaSession.timestamp + maxAge,
        fromCache: true,
        source: 'staticData'
      };
    }
  }
}

console.log('🔄 Iniciando SSO com Apoia usando token PDPJ');
return {
  hasValidSession: false,
  pdpjToken: pdpjToken,
  tokenType: staticData.pdpj_auth?.token_type || 'Bearer'
};
