// FunÃ§Ã£o auxiliar melhorada
function extractCookies(headers) {
  const cookies = {};
  const all = {};

  if (headers['set-cookie']) {
    const setCookies = Array.isArray(headers['set-cookie']) 
      ? headers['set-cookie'] 
      : [headers['set-cookie']];

    setCookies.forEach(cookie => {
      const parts = cookie.split(';');
      const [name, value] = parts[0].split('=');
      if (name && value) {
        const cleanName = name.trim();
        const cleanValue = value.trim();
        all[cleanName] = cleanValue;

        // Identificar session tokens
        if (cleanName.includes('session-token') || 
            cleanName.includes('next-auth')) {
          cookies.sessionToken = cleanValue;
        }
      }
    });
  }

  // Construir cookie string na ordem correta
  const orderedCookies = [];
  const priority = ['__Secure-next-auth.session-token', 'next-auth.session-token'];

  priority.forEach(key => {
    if (all[key]) orderedCookies.push(`${key}=${all[key]}`);
  });

  Object.entries(all).forEach(([k, v]) => {
    if (!priority.includes(k)) {
      orderedCookies.push(`${k}=${v}`);
    }
  });

  return {
    all,
    string: orderedCookies.join('; '),
    sessionToken: cookies.sessionToken
  };
}

