// Gerenciador Global de Tokens e Sess√µes
const staticData = $getWorkflowStaticData('global');

// Inicializar storage
if (!staticData.pdpj_auth) {
  staticData.pdpj_auth = {
    access_token: null,
    refresh_token: null,
    token_type: 'Bearer',
    expires_at: 0,
    timestamp: 0
  };
}

if (!staticData.apoiaSession) {
  staticData.apoiaSession = {
    sessionToken: null,
    cookieString: null,
    cookies: {},
    timestamp: 0,
    expiresAt: 0
  };
}

if (!staticData.cache) {
  staticData.cache = {
    processos: {},
    jurisprudencia: {},
    last_cleanup: Date.now()
  };
}

// Limpeza de cache otimizada
const CACHE_TTL = {
  processos: 3600000, // 1 hora
  jurisprudencia: 86400000 // 24 horas
};

const now = Date.now();
if (now - staticData.cache.last_cleanup > 3600000) {
  let cleaned = 0;

  // Limpar processos antigos
  for (const key in staticData.cache.processos) {
    if (now - staticData.cache.processos[key].timestamp > CACHE_TTL.processos) {
      delete staticData.cache.processos[key];
      cleaned++;
    }
  }

  // Limpar jurisprud√™ncia antiga
  for (const key in staticData.cache.jurisprudencia) {
    if (now - staticData.cache.jurisprudencia[key].timestamp > CACHE_TTL.jurisprudencia) {
      delete staticData.cache.jurisprudencia[key];
      cleaned++;
    }
  }

  staticData.cache.last_cleanup = now;
  console.log(`üßπ Cache cleanup: ${cleaned} itens removidos`);
}

// Status report
console.log(`üìä Status do Sistema:
- Token PDPJ: ${staticData.pdpj_auth.access_token ? 'V√°lido' : 'Ausente'}
- Sess√£o Apoia: ${staticData.apoiaSession.sessionToken ? 'V√°lida' : 'Ausente'}
- Cache Processos: ${Object.keys(staticData.cache.processos).length} itens
- Cache Jurisprud√™ncia: ${Object.keys(staticData.cache.jurisprudencia || {}).length} itens
`);

return $input.all();

